        private static string GPT4oApiKey = config["GPT4oApiKey"];

        private static string Phi3medium128kinstructEndpoint = config["Phi3medium128kinstructEndpoint"];
        private static string Phi3medium128kinstructApiKey = config["Phi3medium128kinstructApiKey"];

        private static string MistralNemo128kEndpoint = config["MistralNemo128kEndpoint"];
        private static string MistralNemo128kApiKey = config["MistralNemo128kApiKey"];

        private static string CohereCommandRPlusEndpoint = config["CohereCommandRPlusEndpoint"];
        private static string CohereCommandRPlusApiKey = config["CohereCommandRPlusApiKey"];

        private static HttpClient client;
        private static List<ConversationMessage> conversationHistory = new List<ConversationMessage>();
        private static string chatHistoryFilePath = "chat-history.json"; // Path to save the chat history
        private static int currentHistoryIndex = -1; // Index of the current selected message in history

        static async Task Main(string[] args)
        {
            string apiKey = CohereCommandRPlusApiKey;
            client = new HttpClient();
            client.Timeout = Timeout.InfiniteTimeSpan;
            client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", apiKey);
            client.BaseAddress = new Uri(CohereCommandRPlusEndpoint);

            // Initialize the chat history file
            await InitializeChatHistoryFile();

            while (true)
            {
                Console.Write("You: "); // Prompt for user input

                // Handle arrow key input for reusing previous messages
                if (Console.CursorLeft == 0 && Console.CursorTop > 0)
                {
                    ConsoleKeyInfo keyInfo = Console.ReadKey(true);
                    if (keyInfo.Key == ConsoleKey.UpArrow)
                    {
                        MoveHistoryIndexUp();
                        Console.Write(conversationHistory[currentHistoryIndex].Content);
                    }
                    else if (keyInfo.Key == ConsoleKey.DownArrow)
                    {
                        MoveHistoryIndexDown();
                        Console.Write(conversationHistory[currentHistoryIndex].Content);
                    }
                    else
                    {
                        Console.Write("\nYou: "); // Reset prompt if not arrow key
                    }
                }

                string userInput = Console.ReadLine();

                // Stop chat if the user said exit
                if (userInput.ToLower() == "exit")
                {
                    break;
                }

                // Reset history index if user types a new message
                currentHistoryIndex = -1;

                // Save the user's message for the next turn
                conversationHistory.Add(new ConversationMessage { Role = "user", Content = userInput });

                // Log the user's message to the chat history file
                await LogChatMessageToFile(userInput, "user");

                string response = await SendMessageToLLM(client, conversationHistory);
                Console.WriteLine($"LLM: {response}\n"); // Display LLM response

                // Log the LLM's response to the chat history file
                await LogChatMessageToFile(response, "assistant");
            }
        }

        // Move the history index up by one (previous message)
        private static void MoveHistoryIndexUp()
        {
            if (currentHistoryIndex > 0)
            {
                currentHistoryIndex--;
            }
        }

        // Move the history index down by one (next message)
        private static void MoveHistoryIndexDown()
        {
            if (currentHistoryIndex < conversationHistory.Count - 1)
            {
                currentHistoryIndex++;
            }
            else
            {
                currentHistoryIndex = -1; // Reset to -1 if at the end
            }
        }

        // Send a message to the LLM and return its response
        static async Task<string> SendMessageToLLM(HttpClient client, List<ConversationMessage> conversationHistory)
        {
            var messages = new List<object>
            {
                // new { role = "system", content = "expert programmer helper" },
                new { role = "user", content = GetCurrentMessageContent() },
                new { role = "assistant", content = "ok, I've parsed the code, how may I help with it?" }
            };

            foreach (var message in conversationHistory)
            {
                messages.Add(new { role = message.Role, content = message.Content });
            }

            var requestBodyObject = new
